        hide.applyDesign()
        datid.applyDesign()
        perfectum.applyDesign()
        sentence.applyDesign()
        nepali.applyDesign()
        
    }

   
    @IBAction func hide(_ sender: UIButton) {
        result.isHidden = true
        
    }
    
    @IBAction func btnsClicked(_ sender: UIButton) {
    switch sender {
            case infinitiv:
                result.text = infinitivWord[timesClicked - 1]
                result.isHidden = false
            case nutid:
                result.text = nutidWord[timesClicked - 1]
                result.isHidden = false

            case datid:
               result.text = datidWord[timesClicked - 1]
               result.isHidden = false

            case perfectum:
               result.text = perfectumWord[timesClicked - 1]
               result.isHidden = false

            case nepali:
                result.text = nepaliWord[timesClicked - 1]
                result.isHidden = false

            case sentence:
               result.text = sentenceWord[timesClicked - 1]
               result.isHidden = false

            default:
                break
                
            }
    }
    @IBAction func nextClicked(_ sender: UIButton) {
        let numberNotInArray = timesClicked + 1
        
        if numberNotInArray > infinitivWord.count {
            timesClicked = 0
            
        }
        else {
            nutid.isEnabled = true
            sentence.isEnabled = true
            datid.isEnabled = true
            infinitiv.isEnabled = true
            perfectum.isEnabled = true
            nepali.isEnabled = true
            //textField.text = ""
            result.text = infinitivWord[timesClicked]
            result.text = nutidWord[timesClicked]
            english.text = enWord[timesClicked]
            
            
            timesClicked = timesClicked + 1
        }
        
       
            
        }
    
    
    
    @IBAction func playClicked(_ sender: Any) {
        myUtterance = AVSpeechUtterance(string:(result.text)!)
        myUtterance.voice = AVSpeechSynthesisVoice(language: "da-DK")
        myUtterance.rate = 0.3
        synth.speak(myUtterance)
    }
  



}

extension UIButton {
    func applyDesign(){
        
        self.layer.cornerRadius = self.frame.height / 2
        
        
    }
    
}












